def populate():
    fake = Faker('pt_BR')
    with app.app_context():
        db.create_all()

        for categoria, produtos_lista in PRODUTOS_POR_CATEGORIA.items():
            print(f"Adicionando produtos para a categoria: {categoria}")

            for nome_produto in produtos_lista:
                # 1. Cria o produto
                p = Produtos(
                    nome=nome_produto,
                    categoria=categoria,
                    quantidade=random.randint(0, 200),
                    descricao=fake.text(max_nb_chars=200),
                    preco=round(random.uniform(5.0, 1000.0), 2)
                )
                db.session.add(p)
                db.session.flush() # Importante para obter o 'p.id' antes do commit

                # 2. Cria a imagem e a associa ao produto
                img = ProdutoImagem(
                    produto_id=p.id,
                    filename=f"{categoria}.png", # Nome do arquivo dinâmico
                    mimetype="image/png",
                    is_primary=True
                )
                db.session.add(img)

            # 3. Faz o commit ao final de cada categoria
            db.session.commit()
            print(f"✔ Categoria '{categoria}' populada com sucesso.")

if __name__ == "__main__":
    populate()